import React from 'react';
import GLOBE from 'vanta/dist/vanta.globe.min';
import './App.css';
import Header from './components/Header/Header';
import Description from './components/Description/Description';
import Converter from './components/Converter/Converter';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.vantaRef = React.createRef();
    this.first='INR';this.second='INR';
    this.state={
      first:'INR',
      second:'INR',
      rate:1
    };
  }
  componentDidMount() {
    this.vantaEffect = GLOBE({
      el: this.vantaRef.current,
      mouseControls: false,
      touchControls: false,
      minHeight: 647.00,
      minWidth: 200.00,
      scale: 1.0,
      scaleMobile: 1.00
    })

  }
  onChangeFirst=(event)=>
{
  first= event.target.value;
  console.log(this.first);
}
onChangeSecond=(event)=>{
  second=event.target.value;
  console.log(this.second);
}
onClick=(event)=>{
  var url='http://www.api.currencylayer.com/live';
  var key='?access_key=a943c2fd5bc1b8b4b75847f5b370c357&format=1';
  var contr='&currencies='+this.state.first+','+this.state.second;

  fetch(url+key+contr).then(response=>{
    return response.json();
    })
    .then(output=>{
      this.setState({rate:output.quotes[this.state.first+this.state.second]});
    });

}
  componentWillUnmount() {
    if (this.vantaEffect) this.vantaEffect.destroy()
  }
  render() {
    return (
      <div className="App">
      <div ref={this.vantaRef}>
        <Header/>
        <Description/>
        <Converter changefirst={this.onChangeFirst} changesecond={this.onChangeSecond}/>
        <button onClick={this.onClick}>Find</button>
        <h1 style={{color:'white'}}>{this.state.rate}</h1>
      </div>

  </div>
  );
  }
}

export default App;
