import  React from 'react';
import List from './List';
import Dropdown from '@bit/campgladiator.cgui.components.atoms.dropdown';
const dropdownOptions = [
	{label: 'option1', value: 'op1'},
	{label: 'option2', value: 'op2'}
]

const defaultOption = { label: 'Select Item', value: ''}


const Converter=()=>{
  return (
    <div style={{display:'flex'}}>
    <Dropdown
  options={dropdownOptions}
  default_={defaultOption}
  style={{minWidth: '160px'}}
    />
    <h3 style={{color:'white',padding:10}}>To</h3>
    <Dropdown
  options={dropdownOptions}
  default_={defaultOption}
  style={{minWidth: '160px'}}
    />
    </div>
  );
}

export default Converter;
